INSERT INTO `study_config` VALUES(18, 2525, 0, 1, 1, 'css/style1.css', 'http://wlux.uw.edu/rbwatson/wluxTaskBar.css', 'http://students.washington.edu/rbwatson/hearts.html', 'http://wlux.uw.edu/rbwatson/end-task.php', 'End task', 'Show', 'Hide', 'This is the first task to do.', NULL, '<script type="text/javascript">		\nfunction listener(event){\n  if ( event.origin !== "http://www.surveygizmo.com" ) {\n	return;\n  }	  \n  if ("surveyStarted" == event.data) {\n	  // hide the continue button\n	  var d = document.getElementById("continueFormDiv");\n	  d.style.display = "none";\n  } else if ("surveyComplete" == event.data) {\n	  // hide the continue button\n	  var d = document.getElementById("continueFormDiv");\n	  d.style.display = "block";\n  } else {\n	  // do nothing\n  }\n}\n</script>\n<p>Please answer the questions below and then press continue.</p>\n<div id="surveyDiv" style="margin-left:auto; margin-right:auto; width:700px;"> </div>\n<script type="text/javascript">\n	document.getElementById("surveyDiv").innerHTML = "<iframe id=\\"surveyFrame\\" src=\\"http://www.surveygizmo.com/s3/1350906/pretest?studyid=" + sessionInfo.studyId + "&conditionid=" + sessionInfo.conditionId + "&sessionid=" + sessionInfo.sessionId + "\\" frameborder=\\"0\\" width=\\"700\\" height=\\"400\\" ></iframe>";	\n	if (window.addEventListener){\n	  addEventListener("message", listener, false);\n	} else {\n	  attachEvent("onmessage", listener);\n	}\n</script>', '<p>You just finished Task 1, Condition 1.', 'http://students.washington.edu/rbwatson/hearts.html', 'http://wlux.uw.edu/rbwatson/task-start.php', 1, 'single');
